<?phpnamespace AppBundle\Service;use Symfony\Component\DependencyInjection\ContainerInterface;class Mailer{    private $container = null;    private $name = "Management Devops";    private $to= "noahbabilone@gmail.com";    /**     * Mailer constructor.     * @param ContainerInterface $container     */    public function __construct(ContainerInterface $container)    {        $this->container = $container;    }    public function message($subject, $body)    {        //        $message = \Swift_Message::newInstance();        $message->setFrom($this->container->getParameter('mailer_user'), $this->name)            ->setTo($this->to,$this->name )            ->setSubject($subject)            ->setCharset('utf-8')            ->setBody($body)            ->setReplyTo($this->container->getParameter('mailer_user'), $this->name)            ->setContentType('text/html');        $this->container->get('mailer')->send($message);            }}